import { CST } from "../CST.js";
import { Chunk } from "../classes/Chunk.js";


export class StageScene extends Phaser.Scene {
    constructor() {
        super({
            key: CST.SCENES.STAGE
        })
    }

    init() {

    }

    preload () 
    {
        this.load.spritesheet('playerRun', 'dist/assets/heroes/knight/Run-Sheet.png', { frameWidth: 64, frameHeight: 32 });
        this.load.spritesheet('playerIdle', 'dist/assets/heroes/knight/Idle-Sheet.png', { frameWidth: 32, frameHeight: 32 });
        this.load.image('floor', 'dist/assets/environment/dungeon/Tiles.png', {frameWidth: 16, frameHeight: 16});
        this.load.image('demoTile', 'dist/assets/environment/dungeon/Tile.png', {frameWidth: 16, frameHeight: 16});
        this.load.spritesheet('orcRun', 'dist/assets/enemy/orcs/orc/Run/Run-Sheet.png', {frameWidth: 64, frameHeight: 64});


    }

    create () 
    {

        this.chunkSize = 16;
        this.tileSize = 16;
        this.chunks = [];

        // var mapData = [];

        // for (var y = 0; y < mapHeight; y++)
        // {
        //     var row = [];


        //     for (var x = 0; x < mapWidth; x++)
        //     {
        //         var tileIndex = Phaser.Math.RND.weightedPick(tiles);
        //         row.push(tileIndex);
        //     }

        //     mapData.push(row);
        // }

        // map = this.make.tilemap({ data: mapData, tileWidth: 16, tileHeight: 16 });
        //this.cameras.main.setBounds(0, 0, 800, 600);
        // this.cameras.main.setBounds(0, 0, 999999, 999999);
        //this.bg = this.add.tileSprite(64, 0, 16, 16, 'floor');

        const idleConfig = {
            key: 'idle',
            frames: this.anims.generateFrameNames('playerIdle'),
            frameRate: 4,
            repeat: -1
        };

        this.anims.create(idleConfig);

        const runConfig = {
            key: 'run',
            frames: this.anims.generateFrameNames('playerRun'),
            frameRate: 6,
            repeat: -1
        };

        this.anims.create(runConfig);

        const orcRunConfig = {
            key: 'orcRun',
            frames: this.anims.generateFrameNames('orcRun'),
            frameRate: 6,
            repeat: -1
        };

        this.anims.create(orcRunConfig);

        // var tileset = map.addTilesetImage('floor');
        // var layer = map.createLayer(0, tileset, 0, 0);
        // map.createLayer()
        // console.log(layer);

        this.cursors = this.input.keyboard.createCursorKeys();

        this.orc = this.add.sprite(0, 0)
        this.orc.anims.play('orcRun');
        this.orc.depth = 1;

        this.player = this.add.sprite(0, 0);
        this.player.anims.play('idle');
        // this.ship = this.add.image(400, 100, 'ship').setAngle(90);

        // this.cameras.main.startFollow(this.player, true, 0.08, 0.08);
        this.cameras.main.setZoom(2);
        this.cameras.main.startFollow(this.player);

        this.followPoint = new Phaser.Math.Vector2(
            this.cameras.main.worldView.x + (this.cameras.main.worldView.width * 0.5),
            this.cameras.main.worldView.y + (this.cameras.main.worldView.height * 0.5)
        );
        this.player.depth = 1;

    }

    getChunk(x, y) {
        var chunk = null;
        for (var i = 0; i < this.chunks.length; i++) {
            if (this.chunks[i].x == x && this.chunks[i].y == y) {
                chunk = this.chunks[i];
            }
        }
        return chunk;
    }

    update () 
    {

        this.followPoint = new Phaser.Math.Vector2(
            this.cameras.main.worldView.x + (this.cameras.main.worldView.width * 0.5),
            this.cameras.main.worldView.y + (this.cameras.main.worldView.height * 0.5)
        );

        var snappedChunkX = (this.chunkSize * this.tileSize) * Math.round(this.followPoint.x / (this.chunkSize * this.tileSize));
        var snappedChunkY = (this.chunkSize * this.tileSize) * Math.round(this.followPoint.y / (this.chunkSize * this.tileSize));

        snappedChunkX = snappedChunkX / this.chunkSize / this.tileSize;
        snappedChunkY = snappedChunkY / this.chunkSize / this.tileSize;

        for (var x = snappedChunkX - 2; x < snappedChunkX + 2; x++) {
            for (var y = snappedChunkY - 2; y < snappedChunkY + 2; y++) {
                var existingChunk = this.getChunk(x, y);

                if (existingChunk == null) {
                    var newChunk = new Chunk(this, x, y);
                    this.chunks.push(newChunk);
                }
            }
        }

        for (var i = 0; i < this.chunks.length; i++) {
            var chunk = this.chunks[i];

            if (Phaser.Math.Distance.Between(
                snappedChunkX,
                snappedChunkY,
                chunk.x,
                chunk.y
            ) < 3) {
                if (chunk !== null) {
                    chunk.load();
                }
            }
            else {
                if (chunk !== null) {
                    chunk.unload();
                }
            }

        this.orc.x += 0.06;
        }


        // sx += 4;

        // if (sx === 16)
        // {
        //     //  Reset and create new strip

        //     var tile;
        //     var prev;

        //     for (var y = 0; y < mapHeight; y++)
        //     {
        //         for (var x = 1; x < mapWidth; x++)
        //         {
        //             tile = map.getTileAt(x, y);
        //             prev = map.getTileAt(x - 1, y);

        //             prev.index = tile.index;

        //             if (x === mapWidth - 1)
        //             {
        //                 tile.index = Phaser.Math.RND.weightedPick(tiles);
        //             }
        //         }
        //     }

        //     sx = 0;
        // }

        if (this.cursors.left.isDown)
        {
            if (this.player.anims.currentAnim.key == 'idle') {
                this.player.play('run');
            }
            this.player.flipX = true;
            this.player.x -= 2.5;
        }
        else if (this.cursors.right.isDown)
        {
            if (this.player.anims.currentAnim.key == 'idle') {
                this.player.play('run');
            }
            this.player.flipX = false;

            this.player.x += 2.5;
        }

        if (this.cursors.up.isDown)
        {
            if (this.player.anims.currentAnim.key == 'idle') {
                this.player.play('run');
            }
            this.player.y -= 2.5;
        }
        else if (this.cursors.down.isDown)
        {
            if (this.player.anims.currentAnim.key == 'idle') {
                this.player.play('run');
            }
            this.player.y += 2.5;

        }

        if (this.player.anims.currentAnim.key != 'idle' && !this.cursors.up.isDown && !this.cursors.down.isDown && !this.cursors.left.isDown && !this.cursors.right.isDown) {
            this.player.play('idle');
        }
    }
}

var map;
var text;
var sx = 0;
var mapWidth = 51;
var mapHeight = 37;
var distance = 0;
var tiles = [20];
